$(document).ready(function(){  
  debugger;
  regionName = getParameterByName('RegionName'); 
  region =DecryptString(regionName);
  $('[name="hdnRegion"]').val(region);

  BindTemplateDetails();
  BindSize();
  BindZone(region);
  BindDiskType_CustomControl();

  BindVPC();

});

function BindTemplateDetails()
{

  var serviceCategoryId =getParameterByName('servicecatalogid');
  if(serviceCategoryId){
   var result=GetTemplateDetails(DecryptString(serviceCategoryId));

   // debugger;

    if(result){
     var data=$.parseJSON(result)[0];
      BindControlValues('hdnTemplatePath',data.TemplatePath);
      BindControlValues('hdnTemplateName',data.TemplateName);
      BindControlValues('hdnSrcImage',data.srcImage);
      BindControlValues('hdnOSCode',data.OsCode);

    }
  }
}

function BindControlValues(ControlId,selectedText)
{

  $("input[id^='"+ControlId+"']").each(function(){             
	 $(this).val(selectedText);
  });  
}


function BindSize()
{
  //debugger;
  var allItemSize =$('select[id^=ddlSize]');

  GetGCPSize('','',true, function(data){
	var jsonData = JSON.parse(data);

	$.each(allItemSize,function(index,control){
      var currentControl = $(this);
      currentControl.empty();
      currentControl.append($('<option>').attr('value', '').html("--Select--"));

      $.each(jsonData, function (i, item) 
             {
               currentControl.append($('<option>').attr('value', item.MachineType).html(item.DiskType)); 
             });
    });

  });  
}

function BindZone(region)
{
  var allZone =$('select[id^=ddlZone]');
  $.each(allZone,function(index,control){
	GetGCPZone(region,control,false); 
  });
}

function BindDiskType_CustomControl()
{
  //debugger;
  var allItemSize =$('select[tag=DiskType]');

  GetCodeTypeMaster('gcpdisktype','','',false, function(data){
    var JsonData =JSON.parse(data);

    $.each(allItemSize,function(index,control)
    {
      var currentControl = $(this);
      currentControl.empty();
      currentControl.append($('<option>').attr('value', '').html("--Select--"));

      $.each(JsonData, function (i, item) 
             {
               currentControl.append($('<option>').attr('data-OptionalField1', item.Code)
												.attr('data-OptionalField2', item.OptionalField2)
												.attr('data-OptionalField3', item.OptionalField3)
												.attr('value', item.OptionalField1).html(item.text)); 
             });
    }); 
  });  
}

function BindVPC()
{
  var allItemSize =$('select[tag=NetworkInterfaceUrl]');

  GetGCPVpc('','','',true,'', function(data){
    var JsonData =JSON.parse(data);

    $.each(allItemSize,function(index,control)
    {

      var currentControl = $(this);
	  var controlselectedvalue = currentControl.attr("controlselectedvalue");
	  currentControl.attr("controlselectedvalue", "");
      currentControl.empty();
      currentControl.append($('<option>').attr('value', '').html("--Select--"));

      $.each(JsonData, function (i, item) 
             {
               currentControl.append($('<option>').attr('data-selflink', item.SelfLInk)
                                     .attr('data-Name', item.Name)
                                     .attr('value', item.SelfLInk).html(item.text)); 
             });

		if(controlselectedvalue)
		{
			$(this).val(controlselectedvalue).change();
		}

    }); 
  });  
}


function SetSubnetDetails(control)
{

	var currentVPCControl = $(control);
	var selectedVPCName = currentVPCControl.children("option:selected").attr('data-Name');
	var ddlSubnets = currentVPCControl.closest('div[class*=customcontrol]').find('select[tag=SubNetworkInterfaceUrl]');

	GetGCPSubnet(selectedVPCName,'',region,'',true,'', function(data){
		var JsonData =JSON.parse(data);

		$.each(ddlSubnets,function(index,control)
		{
			var currentControl = $(this);
			var controlselectedvalue = currentControl.attr("controlselectedvalue");
			currentControl.attr("controlselectedvalue", "");
			currentControl.empty();
			currentControl.append($('<option>').attr('value', '').html("--Select--"));

			$.each(JsonData, function (i, item) 
			 {
				console.log(item);
			   currentControl.append($('<option>').attr('data-selflink', item.SelfLInk).attr('data-Name', item.Name).attr('value', item.SelfLInk).html(item.text)); 
			 });

			 if(controlselectedvalue)
				$(this).val(controlselectedvalue).change();
		}); 
	});
}

function SetDiskTypeDetails(control)
{

	var currentTypeControl = $(control);

	var selectedDiskType = currentTypeControl.children("option:selected");

	var hdnTypeControl = currentTypeControl.closest('div[class*=customcontrol]').find("[tag='Type']");
	hdnTypeControl.val(selectedDiskType.attr("data-OptionalField2"));

	var hdnDskInterfaceControl = currentTypeControl.closest('div[class*=customcontrol]').find("[tag='Interface']");
	hdnDskInterfaceControl.val(selectedDiskType.attr("data-OptionalField3"));
}

