function GenerateBootDisk()
{

  	$.each($("[name='hdnBootDisk']"), function (index, control) {
	var currentControl = $(this);
	var hdnSrcImageControl = currentControl.closest('div.AllControl').find("[name='hdnSrcImage']");
	var txtNameControl = currentControl.closest('div.AllControl').find("[name='txtName']");
	var osControl = currentControl.closest('div.AllControl').find("[name='hdnOSCode']");

	var bootDiskInfo = '<Type>PERSISTENT</Type>';
	bootDiskInfo += '<AutoDelete>true</AutoDelete>';
	bootDiskInfo += '<Mode>READ_WRITE</Mode>';
	bootDiskInfo += '<DeviceName>'+txtNameControl.val()+'-'+osControl.val()+'</DeviceName>';
	bootDiskInfo += '<SourceImage>'+hdnSrcImageControl.val()+'</SourceImage>';


	currentControl.val(bootDiskInfo);
  });
}



